import { useState } from "react";
import { Container } from "react-bootstrap";
import { AdminBebidas } from "./AdminBebidas";
import { AdminUsuarios } from "./AdminUsuarios";



export default function Admin({setBebidas,bebidas}) {
    const [seccion, setSeccion] = useState('bebidas');
    const [usuarios, setUsuarios] = useState([]);
    return (
        <div>
          <Container>
          {seccion === 'bebidas' &&  <AdminBebidas bebidas={bebidas} setBebidas={setBebidas} /> }
          {seccion === 'usuarios' &&  <AdminUsuarios usuarios={usuarios} setUsuarios={setUsuarios} /> }
          </Container>
        </div>

    )
}
