import axios from 'axios';
import React from 'react';
import { useState } from 'react';
import {Table} from 'react-bootstrap';
import { leerDeLocalStorage } from '../utils/localStorage';
import Bebida from './Bebida';

export default function TablaBebidas ({bebidas})  {
    const [isLoading, setIsLoading] = useState(false);
    const deleteBebida = async (id) => {
        setIsLoading(true);
        const tokenLocal = leerDeLocalStorage('token') || {};
        const headers = { 'x-auth-token': tokenLocal.token };
        await axios.delete(`http://localhost:4000/api/bebidas/${id}`, { headers });
        await this.props.actualizar();
        setIsLoading(false);
    };
    return (
        <Table className="mt-5 mx-auto" style={{ width: '600px' }} striped bordered hover size="sm">
        <tbody>
            {bebidas.length === 0
                ? 'No hay bebidas guardadas'
                : bebidas.map(({ titulo, imagen }, i) => {
                      return (
                          <tr key={i}>
                              <td>
                                  <img src={imagen} alt="" style={{ width: '5rem' }} />
                              </td>
                              <td>{titulo}</td>
                              <td><button onClick={() => deleteBebida(Bebida._id)} ><img src="https://icongr.am/feather/trash.svg?size=50&color=8a0000" /> alt="" </button></td>
                          </tr>
                      );
                  })}
        </tbody>
    </Table>
);
                }
