import { useState } from 'react';
import { Button } from 'react-bootstrap';
import Card from 'react-bootstrap/Card';
import { Link } from 'react-router-dom';


export default function Bebida({ product, carrito, setCarrito }) {
    const [isInCart, setIsInCart] = useState(false);

    const addToCart = () => {
        setCarrito((cart) => cart.concat({ product, cantidad: 1 }));
    };

    useEffect(() => {
        const inCart = carrito.find((productCart) => productCart.product.id === product.id);
        if (inCart) {
            setIsInCart(true);
        }
    }, [carrito]);

    return (
        <Card className="card-bebida">
            <Card.Img variant="top" src={props.bebida.imagen} />
            <Card.Body>
                <Card.Title>{props.bebida.titulo}</Card.Title>
                {/* <span>{props.meme.publishedAt}</span> */}
                <p>{props.bebida.precio}</p>

            </Card.Body>

            <Button as={Link} to={`/producto/${props.bebida._id}`}>
                detalle
            </Button>
            <button
                    disabled={isInCart}
                    className={
                        isInCart ? 'btn btn-secondary' : 'btn border-success btn-cart pulsating-circle'
                    }
                    onClick={addToCart}
                >
                    {isInCart ? (
                        'Esta en el carrito'
                    ) : (
                        <img src="https://icongr.am/material/cart.svg?size=30&color=2ca049" alt="" />
                    )}
                </button>
        </Card>
    );
}
